package BasicElseIfOnly;

public class PrintSumAndAverage {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		int b=15;
		int c=68;
		int sum=a+b+c;
		System.out.println("sum  >>"+ sum);
		System.out.println("average  >>"+(a+b+c)/3);
		

	}

}
