package VeryBasics;

import java.util.Scanner;

public class Concatenate_Two_Strings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner sc=new Scanner (System.in);
	        String a=sc.next();
	        String b=sc.next();
	        sc.close();
	        System.out.print(a+b);

	}

}
